mapper.select:
select

from sys_menu


    	and id = #{id}


    	and del_flag = 1


    	and parent_id = #{parentId}


    	and name = #{name}


    	and description = #{description}


    	and href = #{href}


    	and target = #{target}


    	and icon = #{icon}


    	and sort = #{sort}


    	and permission = #{permission}


    	and is_show = #{isShow}


    	and create_time = #{createTime}


    	and create_by = #{createBy}


    	and update_time = #{updateTime}


    	and update_by = #{updateBy}


    	and remark = #{remark}
